<?phpclass BaseModel {    public $errors = array();    public $__columns = "";    public $__params = "";    public $__page_size = 10;    function __construct() {        $metaobj = new ReflectionObject($this);        $classname = $metaobj->getName();        $c = new ReflectionClass($classname);        foreach ($c->getProperties() as $p) {            if ($p->getName() == "errors" || substr(trim($p->getName()), 0, 2) == "__") {                continue;            }            $this->errors[$p->getName()] = "";            $this->__columns = $this->__columns . $p->getName() . ",";            $this->__params = $this->__params . "?,";        }        if ($this->__columns != "") {            $this->__columns = substr($this->__columns, 0, strlen($this->__columns) - 1);            $this->__params = substr($this->__params, 0, strlen($this->__params) - 1);        }    }    public function haserrors() {        $count = 0;        foreach ($this->errors as $key => $value) {            if ($value != "") {                //   echo "<hr />{$value}<hr />";                $count++;            }        }//for        return $count != 0;    }    public function filetered_errors() {        $filetered_errors_array=array();        foreach ($this->errors as $key => $value) {            if ($value != "") {                $filetered_errors_array[$key]=$value;            }        }//for        return $filetered_errors_array;    }    public static function getParams($columns) {        $s = "";        $x = explode(",", $columns);        $count = count($x);        if ($count == 0) {            return "";        }        if ($count > 1) {            for ($i = 0; $i < count($x) - 1; $i++) {                $s = $s . "?,";            }        }        $s = $s . "?";        return $s;    }    public static function Skip($pageno, $pagesize) {        return $pagesize * ($pageno - 1);    }    public static function Limit($pageno, $pagesize) {            $skip = $pagesize * ($pageno-1 );        return "limit {$skip},{$pagesize}";    }//class}