<?phpclass dbmodel extends BaseModel {    function __construct() {        parent::__construct();        //$this->title=Validator::Key($keys['layout_welcome_index_label');    }    public static function get_lang($language=FALSE) {        if ($language != FALSE) {            return $language;            //echo "HAHAHAHHAA";        }        return "english";    }    public static function Delete($table, $id) {        $mysqli = Config::OpenDBConnection();        $query = "delete from {$table} where id =?";        $stmt = Config::CreateStatement($mysqli, $query);        $stmt->bind_param("i", $id);        $stmt->execute();        $mysqli->close();    }    public static function show($table, $id) {        $mysqli = Config::OpenDBConnection();        $query = "update {$table} set isvisible ='y' where id =?";        $stmt = Config::CreateStatement($mysqli, $query);        $stmt->bind_param("i", $id);        $stmt->execute();        $mysqli->close();    }    public static function hide($table, $id) {        $mysqli = Config::OpenDBConnection();        $query = "update {$table} set isvisible ='n' where id =?";        $stmt = Config::CreateStatement($mysqli, $query);        $stmt->bind_param("i", $id);        $stmt->execute();        $mysqli->close();    }    public static function foo($datum) {        $mysqli = Config::OpenDBConnection();        $query = "insert into foo(datum) values('{$datum}')";        $stmt = Config::CreateStatement($mysqli, $query);        $stmt->execute();        $mysqli->close();    }    public static function PageCount($model_name, $where_clause=null, $pagesize=10) {        $query = "select count(*) from {$model_name} ";        if ($where_clause != null) {            $query = $query . " where {$where_clause}";        }                $mysqli = Config::OpenDBConnection();        $stmt = Config::CreateStatement($mysqli, $query);        $stmt->bind_result($count);        $stmt->execute();        if ($stmt->fetch()) {            $mysqli->close();            $r=$count%$pagesize;            $count=$count-$r;            if($r!=0){            return ($count / $pagesize)+1;            }            else            {            return ($count / $pagesize);            }        }        $mysqli->close();        return 0;    }//Fn}//class?>