<?phpclass Pair {    public $textfield;    public $valuefield;    public function __construct($textfield, $valuefield="") {        $this->textfield = $textfield;        if ($valuefield == "") {            $this->valuefield = $textfield;        } else {            $this->valuefield = $valuefield;        }    }    public static function ListForSequence($from, $to) {        $list = array();        for ($i = $from; $i <= $to; $i++) {            $list[] = new Pair($i);        }        return $list;    }}?>